// components/ProfessionalDevelopment.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfessionalDevelopment = ({ identityNumber }) => {
    const [qualifications, setQualifications] = useState([]);
    const [qualification, setQualification] = useState('');
    const [type, setType] = useState('academic');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const fetchQualifications = async () => {
            try {
                const response = await axios.get(`/api/development/${identityNumber}`);
                setQualifications(response.data);
                setLoading(false);
            } catch (err) {
                setError('Error fetching qualifications');
                setLoading(false);
            }
        };

        fetchQualifications();
    }, [identityNumber]);

    const handleAddQualification = async (e) => {
        e.preventDefault();
        const newQualification = { identityNumber, qualification, type };
        try {
            await axios.post(`/api/development`, newQualification);
            setQualification('');
            setType('academic');
            // Refresh qualifications
            const response = await axios.get(`/api/development/${identityNumber}`);
            setQualifications(response.data);
            setSuccessMessage('Qualification added successfully!');
        } catch (err) {
            setError('Error adding qualification');
        }
    };

    if (loading) {
        return <p>Loading qualifications...</p>;
    }

    return (
        <div>
            <h2>Professional Development</h2>
            <form onSubmit={handleAddQualification}>
                <input
                    type="text"
                    value={qualification}
                    onChange={(e) => setQualification(e.target.value)}
                    placeholder="Qualification"
                    required
                />
                <select value={type} onChange={(e) => setType(e.target.value)}>
                    <option value="academic">Academic</option>
                    <option value="professional">Professional</option>
                </select>
                <button type="submit">Add Qualification</button>
            </form>
            {successMessage && <p>{successMessage}</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {qualifications.map((qual) => (
                    <li key={qual._id}>
                        {qual.qualification} - {qual.type}
                        {/* You can add update and delete functionality here */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProfessionalDevelopment;
