// EmployeeForm.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css'; 

const EmployeeForm = ({ fetchEmployees, editingEmployee, setEditingEmployee, employeeData, setEmployeeData }) => {
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (editingEmployee) {
            setEmployeeData(employeeData);
        } else {
            setEmployeeData({
                staffNumber: '',
                fullName: '',
                identityNumber: '',
                qualifications: [''],
                position: '',
                salary: '',
                professionalDevelopment: { activities: [], points: 0 }
            });
        }
    }, [editingEmployee, employeeData, setEmployeeData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData({ ...employeeData, [name]: value });
    };

    const handlePDActivityChange = (activity) => {
        const newActivities = employeeData.professionalDevelopment.activities.includes(activity)
            ? employeeData.professionalDevelopment.activities.filter(a => a !== activity)
            : [...employeeData.professionalDevelopment.activities, activity];
        setEmployeeData({ ...employeeData, professionalDevelopment: { ...employeeData.professionalDevelopment, activities: newActivities } });
    };

    const handlePDPointsChange = (e) => {
        const points = e.target.value;
        setEmployeeData({ ...employeeData, professionalDevelopment: { ...employeeData.professionalDevelopment, points } });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        try {
            const url = editingEmployee ? `http://localhost:5000/api/employees/${employeeData.id}` : 'http://localhost:5000/api/employees';
            const method = editingEmployee ? 'put' : 'post';
            const response = await axios[method](url, employeeData);
            setSuccessMessage(`Employee ${editingEmployee ? 'updated' : 'added'} successfully!`);
            fetchEmployees();
            setEditingEmployee(null);
            setEmployeeData({
                staffNumber: '',
                fullName: '',
                identityNumber: '',
                qualifications: [''],
                position: '',
                salary: '',
                professionalDevelopment: { activities: [], points: 0 }
            });
        } catch (error) {
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data.message || 'Error processing request. Please try again.');
            } else {
                setErrorMessage('Error processing request. Please try again.');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="employee-form"> 
            <h2>{editingEmployee ? 'Edit Employee' : 'Add Employee'}</h2>
            <form onSubmit={handleSubmit}>
                <input name="staffNumber" value={employeeData.staffNumber} onChange={handleChange} placeholder="Staff Number" required />
                <input name="fullName" value={employeeData.fullName} onChange={handleChange} placeholder="Full Name" required />
                <input name="identityNumber" value={employeeData.identityNumber} onChange={handleChange} placeholder="Identity Number" required />
                
                <h3>Professional Development</h3>
                <div>
                    <label>
                        <input type="checkbox" checked={employeeData.professionalDevelopment.activities.includes('Training')} onChange={() => handlePDActivityChange('Training')} />
                        Training
                    </label>
                    <label>
                        <input type="checkbox" checked={employeeData.professionalDevelopment.activities.includes('Certification')} onChange={() => handlePDActivityChange('Certification')} />
                        Certification
                    </label>
                    <label>
                        <input type="checkbox" checked={employeeData.professionalDevelopment.activities.includes('Workshop')} onChange={() => handlePDActivityChange('Workshop')} />
                        Workshop
                    </label>
                </div>
                <input type="number" value={employeeData.professionalDevelopment.points} onChange={handlePDPointsChange} placeholder="Points" required />

                <button type="submit" disabled={loading}>{loading ? 'Processing...' : (editingEmployee ? 'Update Employee' : 'Add Employee')}</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
};

export default EmployeeForm;
