// EmployeeForm.js
import React, { useState } from 'react';
import axios from 'axios';
import '../App.css'; 

const EmployeeForm = ({ fetchEmployees }) => {
    const [employee, setEmployee] = useState({
        staffNumber: '',
        fullName: '',
        identityNumber: '',
        qualifications: [''],
        position: '',
        salary: '',
        professionalDevelopment: {
            activities: [],
            points: 0
        }
    });
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee({ ...employee, [name]: value });
    };

    const handlePDActivityChange = (activity) => {
        const newActivities = employee.professionalDevelopment.activities.includes(activity)
            ? employee.professionalDevelopment.activities.filter(a => a !== activity)
            : [...employee.professionalDevelopment.activities, activity];
        setEmployee({ ...employee, professionalDevelopment: { ...employee.professionalDevelopment, activities: newActivities } });
    };

    const handlePDPointsChange = (e) => {
        const points = e.target.value;
        setEmployee({ ...employee, professionalDevelopment: { ...employee.professionalDevelopment, points } });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        try {
            await axios.post('http://localhost:5000/api/employees', employee);
            setSuccessMessage('Employee added successfully with professional development points!');
            fetchEmployees();
            setEmployee({
                staffNumber: '',
                fullName: '',
                identityNumber: '',
                qualifications: [''],
                position: '',
                salary: '',
                professionalDevelopment: { activities: [], points: 0 }
            });
        } catch (error) {
            console.error(error);
            setErrorMessage('Error adding employee. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="employee-form"> 
            <h2>Add Employee</h2>
            <form onSubmit={handleSubmit}>
                {/* Existing input fields */}
                <input name="staffNumber" value={employee.staffNumber} onChange={handleChange} placeholder="Staff Number" required />
                <input name="fullName" value={employee.fullName} onChange={handleChange} placeholder="Full Name" required />
                <input name="identityNumber" value={employee.identityNumber} onChange={handleChange} placeholder="Identity Number" required />
                
                {/* Professional Development Section */}
                <h3>Professional Development</h3>
                <div>
                    <label>
                        <input type="checkbox" onChange={() => handlePDActivityChange('Training')} />
                        Training
                    </label>
                    <label>
                        <input type="checkbox" onChange={() => handlePDActivityChange('Certification')} />
                        Certification
                    </label>
                    <label>
                        <input type="checkbox" onChange={() => handlePDActivityChange('Workshop')} />
                        Workshop
                    </label>
                </div>
                <input type="number" value={employee.professionalDevelopment.points} onChange={handlePDPointsChange} placeholder="Points" required />

                {/* Submit Button */}
                <button type="submit" disabled={loading}>{loading ? 'Adding...' : 'Add Employee'}</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    );
};

export default EmployeeForm;
