// VehicleList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css'; // Ensure you import the CSS file

const VehicleList = () => {
    const [vehicles, setVehicles] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [currentVehicle, setCurrentVehicle] = useState({ id: '', model: '', status: '' });

    const fetchVehicles = async () => {
        const response = await axios.get('http://localhost:5001/api/vehicles');
        setVehicles(response.data);
    };

    useEffect(() => {
        fetchVehicles();
    }, []);

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:5001/api/vehicles/${id}`);
        fetchVehicles(); // Refresh vehicle list
    };

    const handleEdit = (vehicle) => {
        setCurrentVehicle(vehicle);
        setIsEditing(true);
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:5001/api/vehicles/${currentVehicle.id}`, {
            model: currentVehicle.model,
            status: currentVehicle.status,
        });
        setIsEditing(false);
        fetchVehicles(); // Refresh vehicle list
        setCurrentVehicle({ id: '', model: '', status: '' }); // Clear the form
    };

    return (
        <div className="vehicle-list">
            <h2>Vehicle List</h2>
            <ul>
                {vehicles.map((vehicle) => (
                    <li key={vehicle._id}>
                        {vehicle.model} - {vehicle.status}
                        <button onClick={() => handleEdit(vehicle)}>Edit</button>
                        <button onClick={() => handleDelete(vehicle._id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {isEditing && (
                <div className="edit-form">
                    <h3>Edit Vehicle</h3>
                    <form onSubmit={handleUpdate}>
                        <label>Model:</label>
                        <input
                            type="text"
                            value={currentVehicle.model}
                            onChange={(e) => setCurrentVehicle({ ...currentVehicle, model: e.target.value })}
                            required
                        />
                        <label>Status:</label>
                        <input
                            type="text"
                            value={currentVehicle.status}
                            onChange={(e) => setCurrentVehicle({ ...currentVehicle, status: e.target.value })}
                            required
                        />
                        <button type="submit">Update Vehicle</button>
                        <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default VehicleList;
