// EmployeeForm.js
import React, { useState } from 'react';
import axios from 'axios';
import '../App.css'; 

const EmployeeForm = ({ fetchEmployees }) => {
    const [employee, setEmployee] = useState({
        staffNumber: '',
        fullName: '',
        identityNumber: '',
        qualifications: [''], // Start with one empty qualification
        position: '',
        salary: ''
    });
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee({ ...employee, [name]: value });
    };

    const handleQualificationChange = (index, value) => {
        const newQualifications = [...employee.qualifications];
        newQualifications[index] = value;
        setEmployee({ ...employee, qualifications: newQualifications });
    };

    const addQualification = () => {
        setEmployee({ ...employee, qualifications: [...employee.qualifications, ''] });
    };

    const removeQualification = (index) => {
        const newQualifications = employee.qualifications.filter((_, i) => i !== index);
        setEmployee({ ...employee, qualifications: newQualifications });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        try {
            await axios.post('http://localhost:5000/api/employees', employee);
            setSuccessMessage('Employee added successfully!');
            fetchEmployees(); // Refresh employee list
            setEmployee({
                staffNumber: '',
                fullName: '',
                identityNumber: '',
                qualifications: [''], // Reset to one empty qualification
                position: '',
                salary: ''
            });
        } catch (error) {
            console.error(error);
            setErrorMessage('Error adding employee. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="employee-form"> 
            <h2>Add Employee</h2>
            <form onSubmit={handleSubmit}>
                <input name="staffNumber" value={employee.staffNumber} onChange={handleChange} placeholder="Staff Number" required />
                <input name="fullName" value={employee.fullName} onChange={handleChange} placeholder="Full Name" required />
                <input name="identityNumber" value={employee.identityNumber} onChange={handleChange} placeholder="Identity Number" required />
                
                {employee.qualifications.map((qualification, index) => (
                    <div key={index}>
                        <input 
                            value={qualification} 
                            onChange={(e) => handleQualificationChange(index, e.target.value)} 
                            placeholder="Qualification" 
                            required 
                        />
                        <button type="button" onClick={() => removeQualification(index)}>Remove</button>
                    </div>
                ))}
                <button type="button" onClick={addQualification}>Add Qualification</button>

                <input name="position" value={employee.position} onChange={handleChange} placeholder="Position" required />
                <input name="salary" type="number" value={employee.salary} onChange={handleChange} placeholder="Salary" required />
                <button type="submit" disabled={loading}>{loading ? 'Adding...' : 'Add Employee'}</button>
            </form>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
    )
};

export default EmployeeForm;
