import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css'; // Ensure you import the CSS file

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [editingEmployee, setEditingEmployee] = useState(null);
    const [employeeData, setEmployeeData] = useState({
        staffNumber: '',
        fullName: '',
        identityNumber: '',
        qualifications: [],
        position: '',
        salary: ''
    });
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('fullName'); // Default sorting by fullName
    const [sortOrder, setSortOrder] = useState('asc'); // Default ascending order

    const fetchEmployees = async () => {
        const response = await axios.get('http://localhost:5000/api/employees');
        setEmployees(response.data);
    };

    useEffect(() => {
        fetchEmployees();
    }, []);

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:5000/api/employees/${id}`);
        fetchEmployees(); // Refresh employee list
    };

    const handleEdit = (employee) => {
        setEditingEmployee(employee._id);
        setEmployeeData({
            staffNumber: employee.staffNumber,
            fullName: employee.fullName,
            identityNumber: employee.identityNumber,
            qualifications: employee.qualifications,
            position: employee.position,
            salary: employee.salary
        });
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5000/api/employees/${editingEmployee}`, employeeData);
            fetchEmployees(); // Refresh employee list
            setEditingEmployee(null); // Clear editing state
            setEmployeeData({
                staffNumber: '',
                fullName: '',
                identityNumber: '',
                qualifications: [],
                position: '',
                salary: ''
            });
        } catch (error) {
            console.error(error);
        }
    };

    const handleChange = (e) => {
        setEmployeeData({ ...employeeData, [e.target.name]: e.target.value });
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleSort = (key) => {
        if (sortBy === key) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(key);
            setSortOrder('asc');
        }
    };

    const filteredEmployees = employees
        .filter(employee => 
            employee.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
            employee.position.toLowerCase().includes(searchTerm.toLowerCase())
        )
        .sort((a, b) => {
            const aValue = a[sortBy];
            const bValue = b[sortBy];
            if (sortOrder === 'asc') {
                return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
            } else {
                return bValue < aValue ? -1 : bValue > aValue ? 1 : 0;
            }
        });

    return (
        <div className="employee-list">
            <h2>Employee List</h2>
            <input
                type="text"
                placeholder="Search by name or position"
                value={searchTerm}
                onChange={handleSearchChange}
            />
            <div>
                <button onClick={() => handleSort('fullName')}>Sort by Name</button>
                <button onClick={() => handleSort('position')}>Sort by Position</button>
                <button onClick={() => handleSort('salary')}>Sort by Salary</button>
            </div>
            <ul>
                {filteredEmployees.map((employee) => (
                    <li key={employee._id}>
                        {employee.fullName} - {employee.position}
                        <button onClick={() => handleEdit(employee)}>Edit</button>
                        <button onClick={() => handleDelete(employee._id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {editingEmployee && (
                <div className="employee-form">
                    <h3>Edit Employee</h3>
                    <form onSubmit={handleUpdate}>
                        <input name="staffNumber" value={employeeData.staffNumber} onChange={handleChange} placeholder="Staff Number" required />
                        < input name="fullName" value={employeeData.fullName} onChange={handleChange} placeholder="Full Name" required />
                        <input name="identityNumber" value={employeeData.identityNumber} onChange={handleChange} placeholder="Identity Number" required />
                        <input name="position" value={employeeData.position} onChange={handleChange} placeholder="Position" required />
                        <input name="salary" type="number" value={employeeData.salary} onChange={handleChange} placeholder="Salary" required />
                        <button type="submit">Update Employee</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default EmployeeList;
