// ProfessionalDevelopment.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

const ProfessionalDevelopment = () => {
    const [records, setRecords] = useState([]);
    const [employees, setEmployees] = useState([]);
    const [employeeId, setEmployeeId] = useState('');
    const [courseName, setCourseName] = useState('');
    const [dateCompleted, setDateCompleted] = useState('');
    const [provider, setProvider] = useState('');
    const [updateId, setUpdateId] = useState('');
    const [updateCourseName, setUpdateCourseName] = useState('');
    const [updateDateCompleted, setUpdateDateCompleted] = useState('');
    const [updateProvider, setUpdateProvider] = useState('');
    const [deleteId, setDeleteId] = useState('');
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchRecords();
        fetchEmployees();
    }, []);

    const fetchRecords = async () => {
        setLoading(true);
        try {
            const response = await axios.get('/api/professional-development');
            console.log('Fetched records:', response.data); // Debugging line
            setRecords(response.data);
        } catch (error) {
            console.error(error);
            setErrorMessage('Error fetching records.');
        } finally {
            setLoading(false);
        }
    };

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('/api/employees');
            console.log('Fetched employees:', response.data); // Debugging line
            setEmployees(response.data);
        } catch (error) {
            console.error(error);
            setErrorMessage('Error fetching employees.');
        }
    };

    const handleAddRecord = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const newRecord = { employeeId, courseName, dateCompleted, provider };
        try {
            await axios.post('/api/professional-development', newRecord);
            setSuccessMessage('Record added successfully!');
            fetchRecords(); // Refresh the records after adding
            clearAddForm(); // Clear the form fields
        } catch (error) {
            console.error(error);
            setErrorMessage('Error adding record.');
        } finally {
            setLoading(false);
        }
    };

    const handleUpdateRecord = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const updatedRecord = { courseName: updateCourseName, dateCompleted: updateDateCompleted, provider: updateProvider };
        try {
            await axios.put(`/api/professional-development/${updateId}`, updatedRecord);
            setSuccessMessage('Record updated successfully!');
            fetchRecords(); // Refresh the records after updating
            clearUpdateForm(); // Clear the form fields
        } catch (error) {
            console.error(error);
            setErrorMessage('Error updating record.');
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteRecord = async (e) => {
        e.preventDefault();
        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        try {
            await axios.delete(`/api/professional-development/${deleteId}`);
            setSuccessMessage('Record deleted successfully!');
            fetchRecords(); // Refresh the records after deleting
            clearDeleteForm(); // Clear the form fields
        } catch (error) {
            console.error(error);
            setErrorMessage('Error deleting record.');
        } finally {
            setLoading(false);
        }
    };

    const clearAddForm = () => {
        setEmployeeId('');
        setCourseName('');
        setDateCompleted('');
        setProvider('');
    };

    const clearUpdateForm = () => {
        setUpdateId('');
        setUpdateCourseName('');
        setUpdateDateCompleted('');
        setUpdateProvider('');
    };

    const clearDeleteForm = () => {
        setDeleteId('');
    };

    return (
        <div className="professional-development">
            <h2>Manage Professional Development</h2>
            {successMessage && <p className="success-message">{successMessage}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            
            <div>
                <h3>Add Professional Development Record</h3>
                <form onSubmit={handleAddRecord}>
                    <select value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} required>
                        <option value="">Select Employee</option>
                        {employees.map((employee) => (
                            <option key={employee.id} value={employee.id}>
                                {employee.name}
                            </option>
                        ))}
                    </select>
                    <input
                        type="text"
                        placeholder="Course Name"
                        value={courseName}
                        onChange={(e) => setCourseName(e.target.value)}
                        required
                    />
                    <input
                        type="date"
                        value={dateCompleted}
                        onChange={(e) => setDateCompleted(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Provider"
                        value={provider}
                        onChange={(e) => setProvider(e.target.value)}
                        required
                    />
                    <button type="submit" disabled={loading}>
                        {loading ? 'Adding...' : 'Add Record'}
                    </button>
                </form>
            </div>

            <div>
                <h3>Update Professional Development Record</h3>
                <form onSubmit={handleUpdateRecord}>
                    <input
                        type="text"
                        placeholder="Record ID"
                        value={updateId}
                        onChange={(e) => setUpdateId(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Updated Course Name"
                        value={updateCourseName}
                        onChange={(e) => setUpdateCourseName(e.target.value)}
                    />
                    <input
                        type="date"
                        value={updateDateCompleted}
                        onChange={(e) => setUpdateDateCompleted(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Updated Provider"
                        value={updateProvider}
                        onChange={(e) => setUpdateProvider(e.target.value)}
                    />
                    <button type="submit" disabled={loading}>
                        {loading ? 'Updating...' : 'Update Record'}
                    </button>
                </form>
            </div>

            <div>
                <h3>Delete Professional Development Record</h3>
                <form onSubmit={handleDeleteRecord}>
                    <input
                        type="text"
                        placeholder="Record ID"
                        value={deleteId}
                        onChange={(e) => setDeleteId(e.target.value)}
                        required
                    />
                    <button type="submit" disabled={loading}>
                        {loading ? 'Deleting...' : 'Delete Record'}
                    </button>
                </form>
            </div>

            <div>
                <h3>Current Records</h3>
                {loading ? (
                    <p>Loading records...</p>
                ) : (
                    <ul>
                        {records.length > 0 ? (
                            records.map((record) => (
                                <li key={record.id}>
                                    Employee ID: {record.employeeId}, Course: {record.courseName}, Date Completed: {record.dateCompleted}, Provider: {record.provider}
                                </li>
                            ))
                        ) : (
                            <p>No records found.</p>
                        )}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default ProfessionalDevelopment;
