// ProfessionalDevelopment.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

const ProfessionalDevelopment = () => {
    const [records, setRecords] = useState([]);
    const [employeeId, setEmployeeId] = useState('');
    const [courseName, setCourseName] = useState('');
    const [dateCompleted, setDateCompleted] = useState('');
    const [provider, setProvider] = useState('');
    const [updateId, setUpdateId] = useState('');
    const [updateCourseName, setUpdateCourseName] = useState('');
    const [updateDateCompleted, setUpdateDateCompleted] = useState('');
    const [updateProvider, setUpdateProvider] = useState('');
    const [deleteId, setDeleteId] = useState('');

    useEffect(() => {
        fetchRecords();
    }, []);

    const fetchRecords = async () => {
        const response = await axios.get('/api/professional-development');
        setRecords(response.data);
    };

    const handleAddRecord = async (e) => {
        e.preventDefault();
        const newRecord = { employeeId, courseName, dateCompleted, provider };
        await axios.post('/api/professional-development', newRecord);
        fetchRecords(); // Refresh the records after adding
        clearAddForm(); // Clear the form fields
    };

    const handleUpdateRecord = async (e) => {
        e.preventDefault();
        const updatedRecord = { courseName: updateCourseName, dateCompleted: updateDateCompleted, provider: updateProvider };
        await axios.put(`/api/professional-development/${updateId}`, updatedRecord);
        fetchRecords(); // Refresh the records after updating
        clearUpdateForm(); // Clear the form fields
    };

    const handleDeleteRecord = async (e) => {
        e.preventDefault();
        await axios.delete(`/api/professional-development/${deleteId}`);
        fetchRecords(); // Refresh the records after deleting
        clearDeleteForm(); // Clear the form fields
    };

    const clearAddForm = () => {
        setEmployeeId('');
        setCourseName('');
        setDateCompleted('');
        setProvider('');
    };

    const clearUpdateForm = () => {
        setUpdateId('');
        setUpdateCourseName('');
        setUpdateDateCompleted('');
        setUpdateProvider('');
    };

    const clearDeleteForm = () => {
        setDeleteId('');
    };

    return (
        <div className="professional-development">
            <h2>Manage Professional Development</h2>
            <div>
                <h3>Add Professional Development Record</h3>
                <form onSubmit={handleAddRecord}>
                    <label>Employee ID:</label>
                    <input type="text" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} required />
                    
                    <label>Course Name:</label>
                    <input type="text" value={courseName} onChange={(e) => setCourseName(e.target.value)} required />
                    
                    <label>Date Completed:</label>
                    <input type="date" value={dateCompleted} onChange={(e) => setDateCompleted(e.target.value)} required />
                    
                    <label>Provider:</label>
                    <input type="text" value={provider} onChange={(e) => setProvider(e.target.value)} required />
                    
                    <button type="submit">Add Record</button>
                </form>
            </div>
            <div>
                <h3>Update Professional Development Record</h3>
                <form onSubmit={handleUpdateRecord}>
                    <label>Record ID:</label>
                    <input type="text" value={updateId} onChange={(e) => setUpdateId(e.target.value)} required />
                    
                    <label>Course Name:</label>
                    <input type="text" value={updateCourseName} onChange={(e) => setUpdateCourseName(e.target.value)} />
                    
                    <label>Date Completed:</label>
                    <input type="date" value={updateDateCompleted} onChange={(e) => setUpdateDateCompleted(e.target.value)} />
                    
                    <label>Provider:</label>
                    <input type="text" value={updateProvider} onChange={(e) => setUpdateProvider(e.target.value)} />
                    
                    <button type="submit">Update Record</button>
                </form>
            </div>
            <div>
                <h3>Delete Professional Development Record</h3>
                <form onSubmit={handleDeleteRecord}>
                    <label>Record ID:</label>
                    <input type="text" value={deleteId} onChange={(e) => setDeleteId(e.target.value)} required />
                    <button type="submit">Delete Record</button>
                </form>
            </div>
            <div>
                <h3>Existing Records</h3>
                <ul>
                    {records.map(record => (
                        <li key={record._id}>
                            {record.courseName} - {record.dateCompleted} - {record.provider}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ProfessionalDevelopment;
